/**
 * Determine whether the given `input` is **not** `null` or `undefined`.
 *
 * @param {T} input
 *
 * @returns {Boolean}
 *
 * @example
 * isNotNullish() // false
 * isNotNullish(null) // false
 * isNotNullish(undefined) // false
 *
 * isNotNullish(1) // true
 * isNotNullish('') // true
 * isNotNullish(false) // true
 */
export declare function isNotNullish<T>(input: T | undefined | null): input is T;
